import React, { useEffect, useState } from "react";

const Profile = (props) => {
  return(
    <div className="row gap-6">
      <div className="col text-start">
        {props.index}
      </div>
      <div className="col text-start">
        <h2>{props.user.username}</h2>
      </div>
      <div className="col text-end">
        <i>{props.user.score}</i>
      </div>
    </div>
  )
}


const Leaderboard = () => {
  const [userList, setUserList] = useState([])
  let userLength = 1;

  useEffect(() => {
    const url = "http://localhost:5000/api/users"
    fetch(url)
      .then(res => {
        if (!res.ok) {
          throw new Error("Could not fetch user data.")
        }
        return res.json()
      })
      .then(res => {
        setUserList(res.data.users)
      })
      .then(() => console.log("Ok"))
      .catch(err => console.log(err))
  }, [])

  return(
    <div className="container postion-relative">
      <div className="mt-5 ms-2 position-absolute top-0 start-50 translate-middle">
        <h1 className='creepy-text'>Leaderboard</h1> 
        <h1 className='creepy-text'>Leaderboard</h1> 
        <h1 className='creepy-text'>Leaderboard</h1> 
      </div>
      <div className="mt-5 pt-5 container-sm">
        { userList.map((user, i) => {
          return (
            <Profile key={i} user={user} index={userLength++}/>
          )
        })}
      </div>
    </div>
  )
}

export default Leaderboard
