import React, { useEffect, useState } from "react";

const sampleUsers = [
  {
    username: "Wen Kai",
    points: 80200
  },
  {
    username: "Lee Hao Rui",
    points: 23130
  },
  {
    username: "Bryan Toh",
    points: 35200
  },
  {
    username: "Nina Gan",
    points: 21030
  },
  { 
    username: "Benjamin", 
    points: 12540
  }, 
  { 
    username: "Sharifah", 
    points: 52590
  }, 
  { 
    username: "Shannon", 
    points: 42870
  }, 
  { 
    username: "Rachel Goh", 
    points: 78270,
  } 
]

const Profiles = ( {users} ) => {
  return (
    <div>{Item(sampleUsers)}</div>
  )
}

const Item = (data) => {
  return (
    <>
      {
        data.map((val, index) => (
          <div className='container-2' key={index}>
            <h2>
              {index + 1} - {val.username}
              <i><small>{val.points}</small></i>
            </h2>
          </div>
        ))
      }
    </>
  )
}

const sortUsers = (users) => { 
  // sort with asending order 
  return users.sort((a, b) => { 
    if (a.points === b.points) { 
      return b.points - a.points; 
    } else { 
      return b.points - a.points; 
    } 
  }) 
}

const Profile = (props) => {
  return(
    <>
      <div className="col">
        <h2>{props.key + 1}</h2>
      </div>
      <div className="col">
        <h2>{props.user.username}</h2>
      </div>
      <div className="col">
        <i><small>{props.user.score}</small></i>
      </div>
    </>
  )
}


const Leaderboard = () => {
  const [users, setUsers] = useState([])
  const userData = {
    username: "",
    score: 0
  }

  useEffect(() => {
    const url = "http://localhost:5000/api/users"
    fetch(url)
      .then(res => {
        if (!res.ok) {
          throw new Error("Could not fetch user data.")
        }
        return res.json()
      })
      .then(users => setUsers(users))
      .then(() => console.log("Ok"))
      .catch(err => console.log(err))
  }, [])

  return(
    <div className="container-2 center">
      <div className="row creepy-text-container">
        <h1 className='creepy-text'>Leaderboard</h1> 
        <h1 className='creepy-text'>Leaderboard</h1> 
        <h1 className='creepy-text'>Leaderboard</h1> 
      </div>
      <div>
        { users.map((user, i) => {
          return (
            <Profile key={i} user={user}/>
          )
        })}
      </div>
    </div>
  )
}

export default Leaderboard
