import React from "react";

const SetWordModal = (props) => {
  const nodeRef = React.useRef = React.useRef(null)

  return (
    <>
      <CSSTransition
        nodeRef={nodeRef}
        in={props.show}
        unmountOnExit
        timeout={{ enter: 0, exit: 300 }}
      >
        <div ref={nodeRef} className={`modal ${props.show ? 'show' : ''}`}>
          <div className="modal-content">
            <div className="modal-header">
              <h4 className="modal-title">{props.title}</h4>
            </div>
            <div className="modal-body">{props.children}</div>
            <div className="modal-footer">
              <button className="btn" onClick>Play</button>
              <Link to="/"><button className="btn-close">Back to Home</button></Link>
            </div>
          </div>
        </div>
      </CSSTransition>
    </>
  )
}

export default SetWordModal
