import React, { useEffect, useState } from "react";

const Profile = (props) => {
  return(
    <>
      <div className="row">
        <h2>{props.user.username}</h2>
      </div>
      <div className="col">
        <i><small>{props.user.score}</small></i>
      </div>
    </>
  )
}


const Leaderboard = () => {
  const [userList, setUserList] = useState([])

  useEffect(() => {
    const url = "http://localhost:5000/api/users"
    fetch(url)
      .then(res => {
        if (!res.ok) {
          throw new Error("Could not fetch user data.")
        }
        return res.json()
      })
      .then(res => setUserList(res.data.users))
      .then(() => console.log("Ok"))
      .catch(err => console.log(err))
  }, [])
  console.log(userList)

  return(
    <div className="container-2 center">
      <div className="row creepy-text-container">
        <h1 className='creepy-text'>Leaderboard</h1> 
        <h1 className='creepy-text'>Leaderboard</h1> 
        <h1 className='creepy-text'>Leaderboard</h1> 
      </div>
      <div className="container-2">
        { userList.map((user, i) => {
          return (
            <Profile key={i} user={user}/>
          )
        })}
      </div>
    </div>
  )
}

export default Leaderboard
